import React, {Component, Fragment} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import ShipList from '../../components/ships/ShipList';
import Request from '../../helpers/request';

class ShipContainer extends Component{
  constructor(props){
    super(props);
    this.state = {
      ships: []

    }
  }

  componentDidMount(){
    const request = new Request();
    request.get('/api/ships')
    .then((data) => {
      this.setState({ships: data._embedded.ships})
    })
  }


  render(){
    return (

      <Router>
        <Fragment>
          <Switch>
            <Route render={(props) => {
              return <ShipList ships={this.state.ships} />
              }}/>
              <Route exact path="/ships/:id" render={(props) => {
                const id = props.match.params.id;
                const ship = this.findShipById(id);
                return <ShipDetail ship={ship}/>
              }}/>
          </Switch>
        </Fragment>
      </Router>
    )
  }

}

export default ShipContainer;
